package safego

import (
	"context"
	"fmt"
	"runtime/debug"
	"sync"
	"sync/atomic"
	"time"

	"bigmeanie/pkg/logger"
)

// RecoveryHandler is a function that handles panics
type RecoveryHandler func(recovered interface{}, stack []byte)

// Package logger instance
var log = logger.Component("goroutine")

// Global metrics for monitoring
var (
	activeGoroutines  atomic.Int64
	totalPanics       atomic.Int64
	totalRestarts     atomic.Int64
	totalGoroutines   atomic.Int64
)

// Metrics provides goroutine metrics
type Metrics struct {
	Active        int64
	TotalPanics   int64
	TotalRestarts int64
	Total         int64
}

// GetMetrics returns current goroutine metrics
func GetMetrics() Metrics {
	return Metrics{
		Active:        activeGoroutines.Load(),
		TotalPanics:   totalPanics.Load(),
		TotalRestarts: totalRestarts.Load(),
		Total:         totalGoroutines.Load(),
	}
}

// defaultRecoveryHandler logs the panic and stack trace
func defaultRecoveryHandler(recovered interface{}, stack []byte) {
	totalPanics.Add(1)
	log.Errorf("PANIC RECOVERED: %v\nStack trace:\n%s", recovered, stack)
}

// BackoffStrategy defines how to calculate restart delays
type BackoffStrategy interface {
	NextDelay(attempt int) time.Duration
	Reset()
}

// ExponentialBackoff implements exponential backoff with jitter
type ExponentialBackoff struct {
	InitialDelay time.Duration
	MaxDelay     time.Duration
	Multiplier   float64
}

// NextDelay returns the next delay duration
func (e *ExponentialBackoff) NextDelay(attempt int) time.Duration {
	if attempt <= 0 {
		return e.InitialDelay
	}

	delay := e.InitialDelay
	for i := 0; i < attempt; i++ {
		delay = time.Duration(float64(delay) * e.Multiplier)
		if delay > e.MaxDelay {
			return e.MaxDelay
		}
	}
	return delay
}

// Reset resets the backoff state (no-op for exponential)
func (e *ExponentialBackoff) Reset() {}

// LinearBackoff implements linear backoff
type LinearBackoff struct {
	InitialDelay time.Duration
	Increment    time.Duration
	MaxDelay     time.Duration
}

// NextDelay returns the next delay duration
func (l *LinearBackoff) NextDelay(attempt int) time.Duration {
	delay := l.InitialDelay + time.Duration(attempt)*l.Increment
	if delay > l.MaxDelay {
		return l.MaxDelay
	}
	return delay
}

// Reset resets the backoff state (no-op for linear)
func (l *LinearBackoff) Reset() {}

// DefaultBackoff returns a sensible default backoff strategy
func DefaultBackoff() BackoffStrategy {
	return &ExponentialBackoff{
		InitialDelay: 100 * time.Millisecond,
		MaxDelay:     30 * time.Second,
		Multiplier:   2.0,
	}
}

// SafeGo runs a function in a goroutine with panic recovery
func SafeGo(name string, fn func(), handlers ...RecoveryHandler) {
	handler := defaultRecoveryHandler
	if len(handlers) > 0 {
		handler = handlers[0]
	}

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer activeGoroutines.Add(-1)
		defer func() {
			if r := recover(); r != nil {
				stack := debug.Stack()
				handler(r, stack)
				log.Errorf("Goroutine '%s' panicked and was recovered", name)
			}
		}()

		log.Debugf("Starting goroutine: %s", name)
		fn()
		log.Debugf("Goroutine completed: %s", name)
	}()
}

// SafeGoContext runs a function with context support and panic recovery
func SafeGoContext(ctx context.Context, name string, fn func(context.Context), handlers ...RecoveryHandler) {
	handler := defaultRecoveryHandler
	if len(handlers) > 0 {
		handler = handlers[0]
	}

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer activeGoroutines.Add(-1)
		defer func() {
			if r := recover(); r != nil {
				stack := debug.Stack()
				handler(r, stack)
				log.Errorf("Goroutine '%s' panicked and was recovered", name)
			}
		}()

		log.Debugf("Starting goroutine: %s", name)
		fn(ctx)
		log.Debugf("Goroutine completed: %s", name)
	}()
}

// SafeGoWithRestart runs a function with automatic restart on panic
// Uses iterative approach to prevent goroutine accumulation
func SafeGoWithRestart(ctx context.Context, name string, fn func(context.Context) error, maxRestarts int, backoff BackoffStrategy, handlers ...RecoveryHandler) {
	handler := defaultRecoveryHandler
	if len(handlers) > 0 {
		handler = handlers[0]
	}

	if backoff == nil {
		backoff = DefaultBackoff()
	}

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer activeGoroutines.Add(-1)
		restartCount := 0

		for {
			// Check if context is cancelled
			select {
			case <-ctx.Done():
				log.Infof("Goroutine '%s' stopped due to context cancellation", name)
				return
			default:
			}

			// Run the function with panic recovery
			panicked := false
			func() {
				defer func() {
					if r := recover(); r != nil {
						panicked = true
						stack := debug.Stack()
						handler(r, stack)
						log.Errorf("Goroutine '%s' panicked (restart %d/%d)", name, restartCount, maxRestarts)
					}
				}()

				if restartCount == 0 {
					log.Debugf("Starting goroutine: %s", name)
				} else {
					log.Debugf("Restarting goroutine: %s (attempt %d/%d)", name, restartCount, maxRestarts)
				}

				if err := fn(ctx); err != nil {
					log.Errorf("Goroutine '%s' returned error: %v", name, err)
				}
			}()

			// If function completed without panic, we're done
			if !panicked {
				log.Debugf("Goroutine completed: %s", name)
				return
			}

			// Check if we should restart
			if restartCount >= maxRestarts {
				log.Errorf("Goroutine '%s' exceeded max restarts (%d), stopping", name, maxRestarts)
				return
			}

			// Calculate backoff delay
			delay := backoff.NextDelay(restartCount)
			restartCount++
			totalRestarts.Add(1)

			log.Warnf("Restarting goroutine '%s' after %v delay (attempt %d/%d)", name, delay, restartCount, maxRestarts)

			// Wait with context cancellation support
			select {
			case <-ctx.Done():
				log.Infof("Goroutine '%s' stopped during restart delay due to context cancellation", name)
				return
			case <-time.After(delay):
				// Continue to restart
			}
		}
	}()
}

// Group manages a group of goroutines with panic recovery and context support
type Group struct {
	ctx     context.Context
	cancel  context.CancelFunc
	wg      sync.WaitGroup
	name    string
	handler RecoveryHandler
	mu      sync.RWMutex
	tasks   map[string]bool // Track active tasks
}

// NewGroup creates a new goroutine group with context
func NewGroup(ctx context.Context, name string, handlers ...RecoveryHandler) *Group {
	handler := defaultRecoveryHandler
	if len(handlers) > 0 {
		handler = handlers[0]
	}

	groupCtx, cancel := context.WithCancel(ctx)

	return &Group{
		ctx:     groupCtx,
		cancel:  cancel,
		name:    name,
		handler: handler,
		tasks:   make(map[string]bool),
	}
}

// Go runs a function in the group with panic recovery
func (g *Group) Go(taskName string, fn func(context.Context)) {
	g.wg.Add(1)

	g.mu.Lock()
	g.tasks[taskName] = true
	g.mu.Unlock()

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer g.wg.Done()
		defer activeGoroutines.Add(-1)
		defer func() {
			g.mu.Lock()
			delete(g.tasks, taskName)
			g.mu.Unlock()
		}()
		defer func() {
			if r := recover(); r != nil {
				stack := debug.Stack()
				g.handler(r, stack)
				log.Errorf("Task '%s' in group '%s' panicked and was recovered", taskName, g.name)
			}
		}()

		log.Debugf("Starting task '%s' in group '%s'", taskName, g.name)
		fn(g.ctx)
		log.Debugf("Task '%s' in group '%s' completed", taskName, g.name)
	}()
}

// Wait waits for all goroutines in the group to complete
func (g *Group) Wait() {
	g.wg.Wait()
}

// Stop cancels the group's context and waits for all tasks to complete
func (g *Group) Stop() {
	g.cancel()
	g.wg.Wait()
}

// ActiveTasks returns the names of currently active tasks
func (g *Group) ActiveTasks() []string {
	g.mu.RLock()
	defer g.mu.RUnlock()

	tasks := make([]string, 0, len(g.tasks))
	for task := range g.tasks {
		tasks = append(tasks, task)
	}
	return tasks
}

// SafeAsync runs an async operation with panic recovery and error handling
func SafeAsync(ctx context.Context, name string, fn func(context.Context) error, errHandler func(error)) {
	SafeGoContext(ctx, name, func(ctx context.Context) {
		if err := fn(ctx); err != nil {
			log.Errorf("Async operation '%s' failed: %v", name, err)
			if errHandler != nil {
				errHandler(err)
			}
		}
	})
}

// Monitor provides continuous monitoring with panic recovery and proper cleanup
type Monitor struct {
	name       string
	fn         func(context.Context) error
	interval   time.Duration
	handler    RecoveryHandler
	errHandler func(error)
	ctx        context.Context
	cancel     context.CancelFunc
	running    atomic.Bool
	done       chan struct{}
	backoff    BackoffStrategy
}

// NewMonitor creates a new monitor with fixed interval
func NewMonitor(ctx context.Context, name string, fn func(context.Context) error, interval time.Duration) *Monitor {
	monitorCtx, cancel := context.WithCancel(ctx)
	return &Monitor{
		name:     name,
		fn:       fn,
		interval: interval,
		handler:  defaultRecoveryHandler,
		ctx:      monitorCtx,
		cancel:   cancel,
		done:     make(chan struct{}),
		backoff:  DefaultBackoff(),
	}
}

// SetRecoveryHandler sets a custom panic recovery handler
func (m *Monitor) SetRecoveryHandler(handler RecoveryHandler) *Monitor {
	m.handler = handler
	return m
}

// SetErrorHandler sets a custom error handler
func (m *Monitor) SetErrorHandler(handler func(error)) *Monitor {
	m.errHandler = handler
	return m
}

// SetBackoff sets a custom backoff strategy for error recovery
func (m *Monitor) SetBackoff(backoff BackoffStrategy) *Monitor {
	m.backoff = backoff
	return m
}

// Start starts the monitor
func (m *Monitor) Start() error {
	if !m.running.CompareAndSwap(false, true) {
		return fmt.Errorf("monitor '%s' is already running", m.name)
	}

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer close(m.done)
		defer m.running.Store(false)
		defer activeGoroutines.Add(-1)
		defer func() {
			if r := recover(); r != nil {
				stack := debug.Stack()
				m.handler(r, stack)
				log.Errorf("Monitor '%s' panicked and stopped", m.name)
			}
		}()

		log.Infof("Starting monitor: %s", m.name)

		ticker := time.NewTicker(m.interval)
		defer ticker.Stop()

		consecutiveErrors := 0

		for {
			select {
			case <-m.ctx.Done():
				log.Infof("Stopping monitor '%s': context cancelled", m.name)
				return
			case <-ticker.C:
				// Run the monitored function with panic recovery
				err := func() (err error) {
					defer func() {
						if r := recover(); r != nil {
							stack := debug.Stack()
							m.handler(r, stack)
							err = fmt.Errorf("panic: %v", r)
						}
					}()
					return m.fn(m.ctx)
				}()

				if err != nil {
					consecutiveErrors++
					log.Errorf("Monitor '%s' error (consecutive: %d): %v", m.name, consecutiveErrors, err)
					if m.errHandler != nil {
						m.errHandler(err)
					}

					// Apply backoff for consecutive errors
					if consecutiveErrors > 1 {
						backoffDelay := m.backoff.NextDelay(consecutiveErrors - 1)
						log.Warnf("Monitor '%s' backing off for %v due to consecutive errors", m.name, backoffDelay)

						select {
						case <-m.ctx.Done():
							return
						case <-time.After(backoffDelay):
							// Continue after backoff
						}
					}
				} else {
					// Reset on success
					if consecutiveErrors > 0 {
						log.Infof("Monitor '%s' recovered after %d consecutive errors", m.name, consecutiveErrors)
						consecutiveErrors = 0
						m.backoff.Reset()
					}
				}
			}
		}
	}()

	return nil
}

// Stop stops the monitor and waits for it to complete
func (m *Monitor) Stop() {
	if m.running.Load() {
		log.Infof("Stopping monitor: %s", m.name)
		m.cancel()
		<-m.done
	}
}

// IsRunning returns whether the monitor is currently running
func (m *Monitor) IsRunning() bool {
	return m.running.Load()
}

// RunSafe is a helper that runs a function with panic recovery and returns any panic as an error
func RunSafe(fn func() error) (err error) {
	defer func() {
		if r := recover(); r != nil {
			stack := debug.Stack()
			err = fmt.Errorf("panic: %v\nStack:\n%s", r, stack)
			totalPanics.Add(1)
		}
	}()

	return fn()
}

// RunSafeContext is like RunSafe but with context support
func RunSafeContext(ctx context.Context, fn func(context.Context) error) (err error) {
	defer func() {
		if r := recover(); r != nil {
			stack := debug.Stack()
			err = fmt.Errorf("panic: %v\nStack:\n%s", r, stack)
			totalPanics.Add(1)
		}
	}()

	return fn(ctx)
}

// Registry tracks all active goroutines for lifecycle management
type Registry struct {
	mu       sync.RWMutex
	routines map[string]*RegisteredRoutine
}

// RegisteredRoutine represents a tracked goroutine
type RegisteredRoutine struct {
	Name      string
	StartTime time.Time
	Context   context.Context
	Cancel    context.CancelFunc
}

// globalRegistry is the singleton registry instance
var globalRegistry = &Registry{
	routines: make(map[string]*RegisteredRoutine),
}

// Register adds a goroutine to the registry
func Register(name string, ctx context.Context, cancel context.CancelFunc) {
	globalRegistry.mu.Lock()
	defer globalRegistry.mu.Unlock()

	globalRegistry.routines[name] = &RegisteredRoutine{
		Name:      name,
		StartTime: time.Now(),
		Context:   ctx,
		Cancel:    cancel,
	}
}

// Unregister removes a goroutine from the registry
func Unregister(name string) {
	globalRegistry.mu.Lock()
	defer globalRegistry.mu.Unlock()

	delete(globalRegistry.routines, name)
}

// StopAll cancels all registered goroutines
func StopAll() {
	globalRegistry.mu.RLock()
	defer globalRegistry.mu.RUnlock()

	for name, routine := range globalRegistry.routines {
		log.Infof("Cancelling registered goroutine: %s", name)
		routine.Cancel()
	}
}

// ListActive returns all active registered goroutines
func ListActive() []string {
	globalRegistry.mu.RLock()
	defer globalRegistry.mu.RUnlock()

	names := make([]string, 0, len(globalRegistry.routines))
	for name := range globalRegistry.routines {
		names = append(names, name)
	}
	return names
}

// TrackedGo runs a goroutine with registration and automatic cleanup
func TrackedGo(ctx context.Context, name string, fn func(context.Context), handlers ...RecoveryHandler) {
	routineCtx, cancel := context.WithCancel(ctx)
	Register(name, routineCtx, cancel)

	handler := defaultRecoveryHandler
	if len(handlers) > 0 {
		handler = handlers[0]
	}

	totalGoroutines.Add(1)
	activeGoroutines.Add(1)

	go func() {
		defer Unregister(name)
		defer cancel()
		defer activeGoroutines.Add(-1)
		defer func() {
			if r := recover(); r != nil {
				stack := debug.Stack()
				handler(r, stack)
				log.Errorf("Tracked goroutine '%s' panicked and was recovered", name)
			}
		}()

		log.Debugf("Starting tracked goroutine: %s", name)
		fn(routineCtx)
		log.Debugf("Tracked goroutine completed: %s", name)
	}()
}

