// Test with multiple assertions
func TestMultipleAssertions(t *testing.T) {
    // Arrange
    input := "test"

    // Act
    result := Process(input)

    // Assert
    if result.Status != "success" {
        t.Errorf("expected status 'success', got %q", result.Status)
    }

