var m sync.Map

// Store
m.Store("key", "value")

// Load
value, ok := m.Load("key")

// LoadOrStore
actual, loaded := m.LoadOrStore("key", "new-value")

// Delete
m.Delete("key")

// Range
m.Range(func(key, value interface{}) bool {
    fmt.Printf("%v: %v\n", key, value)
    return true  // Continue iteration
})
