    Employee
    Name string  // Shadows Person.Name
}

mgr := Manager{
    Employee: emp,
    Name:     "Manager Name",  // This shadows Employee.Person.Name
}

fmt.Println(mgr.Name)              // "Manager Name"
fmt.Println(mgr.Employee.Name)     // "Alice"
fmt.Println(mgr.Employee.Person.Name)  // "Alice"
