# modules/desktop/fonts-themes.nix - Fonts and themes configuration
{ pkgs, ... }:

{
  # Font packages
  fonts = {
    enableDefaultPackages = true;

    packages = with pkgs; [
      # Nerd Fonts (with icons/glyphs for terminals)
      # In NixOS 25.05, use the new nerd-fonts syntax
      nerd-fonts.jetbrains-mono
      nerd-fonts.fira-code
      nerd-fonts.hack

      # Regular programming fonts (without nerd font patches)
      jetbrains-mono
      fira-code
      cascadia-code

      # System fonts
      liberation_ttf      # Free alternatives to Arial, Times New Roman
      noto-fonts          # Google's font family
      noto-fonts-cjk-sans # Chinese, Japanese, Korean
      noto-fonts-emoji    # Emoji support
      font-awesome        # Icon font

      # Additional quality fonts
      inter               # Excellent UI font
      roboto              # Google's material design font
      ubuntu_font_family  # Ubuntu fonts
      source-sans-pro     # Adobe's sans font
      source-serif-pro    # Adobe's serif font

      # Terminal and coding focused
      victor-mono         # Cursive italics
      julia-mono          # Designed for Julia
      ibm-plex            # IBM's font family
      recursive           # Variable font with many styles
    ];

    # Font configuration
    fontconfig = {
      enable = true;

      defaultFonts = {
        serif = [ "Noto Serif" "Liberation Serif" ];
        sansSerif = [ "Inter" "Noto Sans" "Liberation Sans" ];
        monospace = [ "JetBrains Mono" "Fira Code" ];
        emoji = [ "Noto Color Emoji" ];
      };

      # Improve font rendering
      antialias = true;
      hinting = {
        enable = true;
        style = "slight";
      };

      subpixel = {
        rgba = "rgb";
        lcdfilter = "default";
      };
    };
  };

  # GTK themes
  environment.systemPackages = with pkgs; [
    # Popular dark themes
    dracula-theme
    catppuccin-gtk
    nordic
    arc-theme
    materia-theme
    gruvbox-dark-gtk

    # Icon themes
    papirus-icon-theme
    dracula-icon-theme
    numix-icon-theme-circle
    tela-icon-theme

    # Cursor themes
    catppuccin-cursors
    bibata-cursors
    capitaine-cursors
  ];

  # Note: GNOME theme configuration would require dconf module
  # For now, themes can be set manually or through GNOME settings
  # programs.dconf would need home-manager or additional configuration

  # Note: Kitty terminal configuration would require home-manager
  # Can be configured per-user or system-wide with additional setup

  # VS Code theme will use extensions already configured
  # Additional VS Code themes can be added in development/tools.nix

  # Shell prompt themes - Starship can be enabled system-wide
  # Note: This is a basic starship configuration
  # More advanced configuration would require home-manager or per-user setup
}

# modules/desktop/desktop-selector.nix - Easy desktop environment switching
{ config, pkgs, lib, ... }:

let
  # Configure which desktop environments to enable
  # Change these to true to enable, false to disable
  desktopConfig = {
    # Full Desktop Environments
    plasma = true;      # KDE Plasma (currently enabled)
    gnome = false;      # GNOME
    xfce = true;        # XFCE - re-enabled, will configure to work with KDE
    cinnamon = false;   # Cinnamon
    mate = false;       # MATE
    budgie = false;     # Budgie
    pantheon = false;   # Pantheon

    # Window Managers
    i3 = false;         # i3 tiling WM
    awesome = false;    # Awesome WM
    hyprland = false;   # Hyprland (Wayland)
    sway = false;       # Sway (Wayland)

    # Display Manager
    # Only one should be true
    useSddm = true;     # KDE's display manager (works with all)
    useGdm = false;     # GNOME's display manager
    useLightdm = false; # Lightweight display manager
  };
in
{
  # Display Manager Configuration
  services.displayManager.sddm.enable = desktopConfig.useSddm;
  services.xserver.displayManager.gdm.enable = desktopConfig.useGdm;
  services.xserver.displayManager.lightdm.enable = desktopConfig.useLightdm;

  # Desktop Environments
  services.desktopManager.plasma6.enable = desktopConfig.plasma;  # NixOS 25.05 uses Plasma 6
  services.xserver.desktopManager.gnome.enable = desktopConfig.gnome;
  services.xserver.desktopManager.xfce.enable = desktopConfig.xfce;
  services.xserver.desktopManager.cinnamon.enable = desktopConfig.cinnamon;
  services.xserver.desktopManager.mate.enable = desktopConfig.mate;
  services.xserver.desktopManager.budgie.enable = desktopConfig.budgie;
  services.xserver.desktopManager.pantheon.enable = desktopConfig.pantheon;

  # Window Managers
  services.xserver.windowManager.i3 = lib.mkIf desktopConfig.i3 {
    enable = true;
    package = pkgs.i3-gaps;
    extraPackages = with pkgs; [
      i3status
      i3lock
      i3blocks
      rofi
      polybar
      dmenu
      dunst
      picom
      nitrogen
      xss-lock
    ];
  };

  services.xserver.windowManager.awesome = lib.mkIf desktopConfig.awesome {
    enable = true;
    luaModules = with pkgs.luaPackages; [
      vicious
    ];
  };

  programs.hyprland = lib.mkIf desktopConfig.hyprland {
    enable = true;
    xwayland.enable = true;
  };

  programs.sway = lib.mkIf desktopConfig.sway {
    enable = true;
    wrapperFeatures.gtk = true;
    extraPackages = with pkgs; [
      swaylock
      swayidle
      waybar
      wofi
      mako
      grim
      slurp
      wl-clipboard
    ];
  };

  # Additional packages based on what's enabled
  environment.systemPackages = with pkgs; lib.flatten [
    # GNOME extras
    (lib.optionals desktopConfig.gnome [
      gnome-tweaks
      gnome-themes-extra
      gnome-extensions-cli
    ])

    # XFCE extras
    (lib.optionals desktopConfig.xfce [
      xfce.xfce4-whiskermenu-plugin
      xfce.xfce4-pulseaudio-plugin
      xfce.xfce4-clipman-plugin
    ])

    # Hyprland extras
    (lib.optionals desktopConfig.hyprland [
      waybar
      wofi
      mako
      swaylock-effects
      swayidle
      grim
      slurp
      wl-clipboard
      swww  # Wallpaper daemon
      kitty # Terminal that works well with Wayland
    ])

    # Common tools for all environments
    (lib.optionals (desktopConfig.gnome || desktopConfig.xfce || desktopConfig.cinnamon || desktopConfig.mate) [
      networkmanagerapplet
    ])
  ];

  # Wayland support
  environment.sessionVariables = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    # Hint Electron apps to use Wayland
    NIXOS_OZONE_WL = "1";

    # Firefox Wayland support
    MOZ_ENABLE_WAYLAND = "1";

    # QT Wayland support
    QT_QPA_PLATFORM = "wayland";
    QT_WAYLAND_DISABLE_WINDOWDECORATION = "1";

    # SDL Wayland support
    SDL_VIDEODRIVER = "wayland";
  };

  # XDG portal configuration for Wayland compositors
  xdg.portal = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    enable = true;
    extraPortals = with pkgs; [
      xdg-desktop-portal-gtk
      xdg-desktop-portal-wlr
    ];
  };

  # Exclude XFCE notification daemon when both KDE and XFCE are enabled
  # This prevents notification conflicts and ensures KDE notifications work properly
  environment.xfce.excludePackages = lib.mkIf (desktopConfig.plasma && desktopConfig.xfce) [
    pkgs.xfce.xfce4-notifyd
  ];

  # Ensure KDE notification service takes priority when both are installed
  systemd.user.services.ensure-kde-notifications = lib.mkIf (desktopConfig.plasma && desktopConfig.xfce) {
    description = "Ensure KDE notifications are used instead of XFCE";
    wantedBy = [ "graphical-session.target" ];
    serviceConfig = {
      Type = "oneshot";
      RemainAfterExit = true;
      ExecStart = "${pkgs.bash}/bin/bash -c 'mkdir -p ~/.local/share/dbus-1/services/ && ln -sf /run/current-system/sw/share/dbus-1/services/org.kde.plasma.Notifications.service ~/.local/share/dbus-1/services/org.freedesktop.Notifications.service'";
    };
  };
}

# Instructions:
# 1. Edit the desktopConfig at the top of this file
# 2. Set desired desktop environments to true
# 3. Make sure only one display manager is true
# 4. Save and run: sudo nixos-rebuild switch
# 5. Restart and select your desktop at the login screen

# Note: You can have multiple desktop environments installed at once.
# The display manager will show a session selector to choose between them.

