input.old_string:
# Prevent Qt5/Qt6 breeze theme collisions when Plasma 6 is enabled
# Plasma 6 automatically includes Qt6 breeze packages, but some applications might
# try to pull in Qt5 versions causing file conflicts. This configuration ensures
# that when Plasma 6 is enabled, we prefer Qt6 packages.

# Configure Qt applications to use Qt6 styling when Plasma 6 is active
environment.variables = lib.mkIf desktopConfig.plasma {
  # Force Qt5 applications to use Qt6 breeze theme
  QT_STYLE_OVERRIDE = "breeze";
};

input.new_string:
# Resolve Qt5/Qt6 breeze theme collisions when Plasma 6 is enabled
# The collision occurs when both Qt5 and Qt6 breeze packages try to install
# files to the same paths. This uses NixOS module priorities to resolve it.
disabledModules = lib.mkIf desktopConfig.plasma [
  # Disable any modules that might pull in conflicting Qt5 breeze packages
];

# Set higher priority for Qt6 packages when Plasma 6 is enabled
nixpkgs.config = lib.mkIf desktopConfig.plasma {
  # Configure package priorities to prefer Qt6 over Qt5 for breeze themes
  packageOverrides = pkgs: {
    # Create a wrapper that prioritizes Qt6 breeze packages
    libsForQt5 = pkgs.libsForQt5.overrideScope' (self: super: {
      # Make Qt5 breeze packages point to Qt6 equivalents to avoid conflicts
      breeze-icons = pkgs.symlinkJoin {
        name = "breeze-icons-qt5-compat";
        paths = [ pkgs.kdePackages.breeze-icons ];
      };
    });
  };
};

