     2→{ config, pkgs, lib, ... }:
     3→
     4→{
     5→  # Enhanced Git configuration
     6→  programs.git = {
     7→    enable = true;
     8→    userName = "Dominic";
     9→    userEmail = "dominicnunez@gmail.com";
    10→
    11→    # Delta - better git diffs
    12→    delta = {
    13→      enable = true;
    14→      options = {
    15→        navigate = true;
    16→        light = false;
    17→        side-by-side = true;
    18→        line-numbers = true;
    19→        syntax-theme = "Dracula";
    20→        features = "decorations";
    21→        decorations = {
    22→          commit-decoration-style = "bold yellow box ul";
    23→          file-style = "bold yellow ul";
    24→          file-decoration-style = "none";
    25→          hunk-header-decoration-style = "cyan box ul";
    26→        };
    27→      };
    28→    };
    29→
    30→    extraConfig = {
    31→      init.defaultBranch = "main";
    32→      pull.rebase = true;
    33→      push.autoSetupRemote = true;
    34→      core.editor = "nvim";
    35→
    36→      # Better diff and merge
    37→      merge.conflictstyle = "diff3";
    38→      diff.colorMoved = "default";
    39→      diff.algorithm = "histogram";
    40→
    41→      # Performance
    42→      core.preloadindex = true;
    43→      core.fscache = true;
    44→      gc.auto = 256;
    45→
    46→      # Rebase
    47→      rebase.autoStash = true;
    48→      rebase.autoSquash = true;
    49→
    50→      # Fetch
    51→      fetch.prune = true;
    52→      fetch.prunetags = true;
    53→
    54→      # Better log
    55→      log.date = "relative";
    56→      format.pretty = "format:%C(yellow)%h %Cblue%>(12)%ad %Cgreen%<(7)%aN%Cred%d %Creset%s";
    57→
    58→      # URL shortcuts
    59→      url."git@github.com:".insteadOf = "gh:";
    60→      url."git@gitlab.com:".insteadOf = "gl:";
    61→      url."git@bitbucket.org:".insteadOf = "bb:";
    62→    };
    63→
    64→    # Comprehensive Git aliases
    65→    aliases = {
    66→      # Status and info
    67→      st = "status -sb";
    68→      s = "status -s";
    69→
    70→      # Commits
    71→      ci = "commit";
    72→      cm = "commit -m";
    73→      ca = "commit --amend";
    74→      can = "commit --amend --no-edit";
    75→
    76→      # Branches
    77→      br = "branch";
    78→      co = "checkout";
    79→      cob = "checkout -b";
    80→      brd = "branch -d";
    81→      brD = "branch -D";
    82→
    83→      # Staging
    84→      a = "add";
    85→      aa = "add --all";
    86→      ap = "add --patch";
    87→      unstage = "reset HEAD --";
    88→
    89→      # Diffs
    90→      d = "diff";
    91→      ds = "diff --staged";
    92→      dt = "difftool";
    93→
