# modules/home-manager/development.nix - Development tools and Git configuration
{ config, pkgs, lib, ... }:

{
  # Enhanced Git configuration
  programs.git = {
    enable = true;
    userName = "Dominic";
    userEmail = "dominicnunez@gmail.com";

    # Delta - better git diffs
    delta = {
      enable = true;
      options = {
        navigate = true;
        light = false;
        side-by-side = true;
        line-numbers = true;
        syntax-theme = "Dracula";
        features = "decorations";
        decorations = {
          commit-decoration-style = "bold yellow box ul";
          file-style = "bold yellow ul";
          file-decoration-style = "none";
          hunk-header-decoration-style = "cyan box ul";
        };
      };
    };

    extraConfig = {
      init.defaultBranch = "main";
      pull.rebase = true;
      push.autoSetupRemote = true;
      core.editor = "nvim";

      # Better diff and merge
      merge.conflictstyle = "diff3";
      diff.colorMoved = "default";
      diff.algorithm = "histogram";

      # Performance
      core.preloadindex = true;
      core.fscache = true;
      gc.auto = 256;

      # Rebase
      rebase.autoStash = true;
      rebase.autoSquash = true;

      # Fetch
      fetch.prune = true;
      fetch.prunetags = true;

      # Better log
      log.date = "relative";
      format.pretty = "format:%C(yellow)%h %Cblue%>(12)%ad %Cgreen%<(7)%aN%Cred%d %Creset%s";

      # URL shortcuts
      url."git@github.com:".insteadOf = "gh:";
      url."git@gitlab.com:".insteadOf = "gl:";
      url."git@bitbucket.org:".insteadOf = "bb:";
    };

    # Comprehensive Git aliases
    aliases = {
      # Status and info
      st = "status -sb";
      s = "status -s";

      # Commits
      ci = "commit";
      cm = "commit -m";
      ca = "commit --amend";
      can = "commit --amend --no-edit";

      # Branches
      br = "branch";
      co = "checkout";
      cob = "checkout -b";
      brd = "branch -d";
      brD = "branch -D";

      # Staging
      a = "add";
      aa = "add --all";
      ap = "add --patch";
      unstage = "reset HEAD --";

      # Diffs
      d = "diff";
      ds = "diff --staged";
      dt = "difftool";

      # Logs
      l = "log --oneline --graph --decorate";
      lg = "log --graph --pretty=format:'%Cred%h%Creset -%C(yellow)%d%Creset %s %Cgreen(%cr) %C(bold blue)<%an>%Creset' --abbrev-commit";
      lga = "log --graph --pretty=format:'%Cred%h%Creset -%C(yellow)%d%Creset %s %Cgreen(%cr) %C(bold blue)<%an>%Creset' --abbrev-commit --all";
      last = "log -1 HEAD --stat";

      # Remote
      f = "fetch";
      fa = "fetch --all";
      p = "push";
      pf = "push --force-with-lease";
      pl = "pull";

      # Stash
      ss = "stash save";
      sp = "stash pop";
      sl = "stash list";

      # Reset
      r = "reset";
      r1 = "reset HEAD^";
      r2 = "reset HEAD^^";
      rh = "reset --hard";
      rh1 = "reset HEAD^ --hard";
      rh2 = "reset HEAD^^ --hard";

      # Cherry-pick
      cp = "cherry-pick";
      cpa = "cherry-pick --abort";
      cpc = "cherry-pick --continue";

      # Rebase
      rb = "rebase";
      rbi = "rebase -i";
      rbc = "rebase --continue";
      rba = "rebase --abort";
      rbs = "rebase --skip";

      # Utils
      aliases = "config --get-regexp alias";
      whoami = "config user.name";

      # Find
      find = "!git ls-files | grep -i";
      grep = "grep -Ii";
    };

    # Ignore global files
    ignores = [
      # OS
      ".DS_Store"
      "Thumbs.db"
      "*.swp"
      "*.swo"
      "*~"

      # IDE
      ".idea/"
      ".vscode/"
      "*.iml"
      ".project"
      ".classpath"
      ".settings/"

      # Languages
      "__pycache__/"
      "*.pyc"
      "node_modules/"
      ".npm/"
      "target/"
      "*.class"

      # Env
      ".env"
      ".env.local"
      ".envrc"

      # Logs
      "*.log"
      "logs/"

      # Build
      "dist/"
      "build/"
      "out/"
      "*.o"
      "*.so"
      "*.dylib"
      "*.dll"
    ];
  };

  # Lazygit - Terminal UI for git
  programs.lazygit = {
    enable = true;
    settings = {
      gui = {
        theme = {
          selectedLineBgColor = ["reverse"];
          selectedRangeBgColor = ["reverse"];
        };
        showFileTree = true;
        showCommandLog = true;
        showBottomLine = true;
        nerdFontsVersion = "3";
      };

      git = {
        paging = {
          colorArg = "always";
          pager = "delta --dark --paging=never";
        };
        commit = {
          signOff = false;
        };
        merging = {
          manualCommit = false;
          args = "";
        };
      };

      os = {
        editCommand = "nvim";
        editCommandTemplate = "{{editor}} {{filename}}";
        openCommand = "xdg-open {{filename}}";
      };

      keybinding = {
        universal = {
          quit = "q";
          quit-alt1 = "<c-c>";
          return = "<esc>";
          quitWithoutChangingDirectory = "Q";
          togglePanel = "<tab>";
          prevItem = "<up>";
          nextItem = "<down>";
          prevBlock = "<left>";
          nextBlock = "<right>";
          prevPage = "<c-b>";
          nextPage = "<c-f>";
          scrollLeft = "h";
          scrollRight = "l";
          scrollUpMain = "<c-u>";
          scrollDownMain = "<c-d>";
          refresh = "R";
        };
      };

      customCommands = [
        {
          key = "C";
          command = "git cz";
          context = "files";
          description = "commit with commitizen";
        }
      ];
    };
  };

  # Gitui - Fast terminal UI for git (Rust-based)
  programs.gitui = {
    enable = true;
    keyConfig = ''
      (
        open_help: Some(( code: F(1), modifiers: "")),

        move_left: Some(( code: Char('h'), modifiers: "")),
        move_right: Some(( code: Char('l'), modifiers: "")),
        move_up: Some(( code: Char('k'), modifiers: "")),
        move_down: Some(( code: Char('j'), modifiers: "")),

        popup_up: Some(( code: Char('p'), modifiers: "CONTROL")),
        popup_down: Some(( code: Char('n'), modifiers: "CONTROL")),
        page_up: Some(( code: Char('b'), modifiers: "CONTROL")),
        page_down: Some(( code: Char('f'), modifiers: "CONTROL")),
        home: Some(( code: Char('g'), modifiers: "")),
        end: Some(( code: Char('G'), modifiers: "SHIFT")),

        shift_up: Some(( code: Char('K'), modifiers: "SHIFT")),
        shift_down: Some(( code: Char('J'), modifiers: "SHIFT")),

        status_reset_item: Some(( code: Char('U'), modifiers: "SHIFT")),
        diff_reset_lines: Some(( code: Char('u'), modifiers: "")),
        diff_stage_lines: Some(( code: Char('s'), modifiers: "")),

        stashing_save: Some(( code: Char('w'), modifiers: "")),
        stashing_toggle_index: Some(( code: Char('m'), modifiers: "")),

        edit_file: Some(( code: Char('I'), modifiers: "SHIFT")),

        select_status: Some(( code: Tab, modifiers: "")),
        select_branch: Some(( code: Char('b'), modifiers: "")),
        select_commit: Some(( code: Char('l'), modifiers: "")),
        select_stash: Some(( code: Char('s'), modifiers: "")),

        open_commit: Some(( code: Enter, modifiers: "")),
        open_commit_editor: Some(( code: Char('e'), modifiers: "")),

        undo_commit: Some(( code: Char('U'), modifiers: "SHIFT")),

        toggle_diff_stage: Some(( code: Char('s'), modifiers: "")),
        toggle_file_tree: Some(( code: Char('t'), modifiers: "")),

        push: Some(( code: Char('p'), modifiers: "")),
        pull: Some(( code: Char('f'), modifiers: "")),

        quit: Some(( code: Char('q'), modifiers: "")),
      )
    '';
    theme = ''
      (
        selected_tab: Cyan,
        command_fg: Gray,
        selection_bg: Blue,
        cmdbar_bg: Blue,
        cmdbar_extra_lines_bg: Blue,
        disabled_fg: DarkGray,
        diff_line_add: Green,
        diff_line_delete: Red,
        diff_file_added: LightGreen,
        diff_file_removed: LightRed,
        diff_file_moved: LightMagenta,
        diff_file_modified: Yellow,
        commit_hash: Magenta,
        commit_time: LightCyan,
        commit_author: Green,
        danger_fg: Red,
        push_gauge_bg: Blue,
        push_gauge_fg: Reset,
        tag_fg: LightMagenta,
        branch_fg: LightYellow,
      )
    '';
  };

  # GitHub CLI
  programs.gh = {
    enable = true;
    settings = {
      git_protocol = "ssh";
      prompt = "enabled";
      editor = "nvim";

      aliases = {
        co = "pr checkout";
        pv = "pr view";
        pc = "pr create";
        pl = "pr list";
        rv = "repo view";
        rc = "repo clone";
        il = "issue list";
        iv = "issue view";
        ic = "issue create";
      };
    };
  };

  # Development packages
  home.packages = with pkgs; [
    # Git tools
    git-lfs
    git-filter-repo
    git-crypt
    gitAndTools.git-extras
    gitAndTools.git-open
    gitAndTools.diff-so-fancy
    gitAndTools.hub
    tig

    # Code formatting and linting
    pre-commit
    commitizen
    nixpkgs-fmt  # Nix code formatter for VS Code and CLI

    # Development utilities
    jq
    yq
    httpie
    curlie

    # Documentation
    mdbook
    pandoc

    # ==== Language-Specific Development Tools ====

    # Python Development
    python312Full
    python312Packages.pip
    python312Packages.virtualenv
    python312Packages.ipython
    python312Packages.black
    python312Packages.pytest
    poetry
    ruff

    # JavaScript/TypeScript Development
    nodejs_22
    nodePackages.npm
    nodePackages.pnpm
    nodePackages.typescript
    nodePackages.typescript-language-server
    nodePackages.prettier

    # Rust Development
    rustc
    cargo
    rustfmt
    clippy
    rust-analyzer

    # Go Development
    go
    gopls
    golangci-lint

    # Go development tools
    go-outline         # Go code outline
    gomodifytags       # Modify struct tags
    gotests            # Generate tests
    gocode-gomod       # Autocomplete daemon

    # Code quality tools
    govulncheck        # Vulnerability checker
    go-tools           # Additional tools (staticcheck, etc.)

    # Debugging and profiling
    delve              # Debugger
    gore               # REPL
    graphviz           # For pprof graphs

    # Debugging and Profiling
    hyperfine  # Command-line benchmarking
    tokei  # Count lines of code
  ];

  # Language-specific environment variables
  home.sessionVariables = {
    # Python
    PYTHONDONTWRITEBYTECODE = "1";
    PYTHONUNBUFFERED = "1";

    # Update PATH for language tools
    PATH = "$PATH:$HOME/go/bin:$HOME/.cargo/bin:$HOME/.npm-global/bin:$HOME/.local/bin";
  };

  # Shell aliases for development tools
  programs.bash.shellAliases = {
    # Git shortcuts (in addition to git aliases)
    g = "git";
    lg = "lazygit";
    gu = "gitui";

    # GitHub CLI
    ghpr = "gh pr";
    ghis = "gh issue";
    ghrp = "gh repo";

    # Development
    http = "httpie";
    curl = "curlie";

    # Python
    py = "python";
    py3 = "python3";
    ipy = "ipython";
    pip = "pip3";
    venv = "python -m venv";
    activate = "source ./venv/bin/activate";

    # Node.js
    ni = "npm install";
    nr = "npm run";
    ns = "npm start";
    nt = "npm test";
    nb = "npm run build";

    # Rust
    cb = "cargo build";
    cr = "cargo run";
    ct = "cargo test";
    cc = "cargo check";
    cf = "cargo fmt";
    cl = "cargo clippy";

    # Docker
    d = "docker";
    dc = "docker-compose";
    dps = "docker ps";
    dimg = "docker images";

    # Kubernetes
    k = "kubectl";
    kgp = "kubectl get pods";
    kgs = "kubectl get services";
    kgd = "kubectl get deployments";
    kaf = "kubectl apply -f";
    kdel = "kubectl delete";
    klog = "kubectl logs";
    kexec = "kubectl exec -it";
  };

  # Additional language-specific configurations can be added here
  # For example, rustup configuration, poetry config, etc.
}

# modules/home-manager/applications.nix - User desktop applications
{ config, pkgs, lib, ... }:

{
  # Desktop applications for the user
  home.packages = with pkgs; [
    # ===== Browsers =====
    # Browsers are configured in browsers.nix

    # ===== Communication =====
    discord
    # element-desktop  # Matrix client
    # signal-desktop  # Signal messenger

    # ===== Media =====
    spotify
    vlc
    # mpv  # Alternative video player
    # audacity  # Audio editor
    # obs-studio  # Streaming/recording

    # ===== Productivity =====
    obsidian
    libreoffice-fresh  # Office suite
    # logseq  # Alternative knowledge base
    # notion-app-enhanced  # Note-taking
    # zettlr  # Markdown editor

    # ===== Development IDEs =====
    vscode
    # vscodium  # Open source VS Code
    # jetbrains.idea-community  # IntelliJ IDEA
    # sublime4  # Sublime Text
    # zed-editor  # Modern collaborative editor

    # ===== Password Management =====
    enpass
    # bitwarden  # Alternative password manager
    # keepassxc  # Offline password manager

    # ===== System Utilities =====
    flameshot  # Screenshot tool
    # copyq  # Clipboard manager
    peek  # GIF recorder
    ksnip  # Alternative screenshot tool

    # ===== Gaming =====
    # Gaming packages moved to gaming.nix

    # ===== Additional Tools =====
    gimp  # Image editor
    inkscape  # Vector graphics
    krita  # Digital painting
    blender  # 3D modeling
    freecad  # CAD software
  ];

  # Application-specific configurations

  # VS Code configuration
  programs.vscode = {
    enable = true;
    package = pkgs.vscode;

    # Default profile with settings and extensions
    profiles = {
      default = {
        # User settings
        userSettings = {
      "editor.fontSize" = 13;
      "editor.fontFamily" = "'JetBrains Mono', 'Droid Sans Mono', monospace";
      "editor.fontLigatures" = true;
      "editor.lineHeight" = 22;
      "editor.rulers" = [80 120];
      "editor.wordWrap" = "on";
      "editor.minimap.enabled" = false;
      "editor.renderWhitespace" = "selection";
      "editor.suggestSelection" = "first";
      "editor.acceptSuggestionOnCommitCharacter" = false;

      "workbench.colorTheme" = "Dracula";
      "workbench.iconTheme" = "material-icon-theme";
      "workbench.startupEditor" = "none";

      "terminal.integrated.fontSize" = 13;
      "terminal.integrated.fontFamily" = "'JetBrains Mono'";
      "terminal.integrated.defaultProfile.linux" = "bash";

      "files.autoSave" = "afterDelay";
      "files.autoSaveDelay" = 1000;
      "files.trimTrailingWhitespace" = true;
      "files.insertFinalNewline" = true;

      "git.enableSmartCommit" = true;
      "git.confirmSync" = false;
      "git.autofetch" = true;

      "extensions.ignoreRecommendations" = false;
      "telemetry.telemetryLevel" = "off";

      # Language-specific settings
      "[python]" = {
        "editor.formatOnSave" = true;
        "editor.defaultFormatter" = "ms-python.black-formatter";
      };
      "[javascript]" = {
        "editor.formatOnSave" = true;
        "editor.defaultFormatter" = "esbenp.prettier-vscode";
      };
      "[typescript]" = {
        "editor.formatOnSave" = true;
        "editor.defaultFormatter" = "esbenp.prettier-vscode";
      };
      "[rust]" = {
        "editor.formatOnSave" = true;
      };
      "[nix]" = {
        "editor.formatOnSave" = true;
        "editor.defaultFormatter" = "jnoortheen.nix-ide";
      };
    };

        # Extensions to install
        extensions = with pkgs.vscode-extensions; [
      # Themes
      dracula-theme.theme-dracula
      pkief.material-icon-theme

      # General
      # editorconfig.editorconfig
      # streetsidesoftware.code-spell-checker
      # usernamehw.errorlens
      # gruntfuggly.todo-tree

      # Git
      eamodio.gitlens
      mhutchie.git-graph
      donjayamanne.githistory

      # Nix
      jnoortheen.nix-ide

      # Python
      ms-python.python
      ms-python.vscode-pylance
      ms-python.black-formatter
      ms-python.isort

      # JavaScript/TypeScript
      dbaeumer.vscode-eslint
      esbenp.prettier-vscode

      # Rust
      rust-lang.rust-analyzer
      tamasfe.even-better-toml

      # Go
      golang.go

      # Docker/Kubernetes
      ms-azuretools.vscode-docker
      ms-kubernetes-tools.vscode-kubernetes-tools

      # YAML/JSON
      redhat.vscode-yaml

      # Markdown
      yzhang.markdown-all-in-one
      bierner.markdown-mermaid

      # Remote Development
      # ms-vscode-remote.remote-ssh
      # ms-vscode-remote.remote-containers
        ];
      };
    };
  };

  # Browser configurations moved to browsers.nix

  # ===== Media Application Settings =====

  # Spotify configuration
  # Note: Spotify doesn't have a Home Manager module, but we can set preferences
  home.file.".config/spotify-tui/config.yml".text = ''
    # Spotify TUI configuration (if you use spotify-tui)
    theme:
      active: Cyan
      banner: Green
      error_border: Red
      error_text: LightRed
      hint: Yellow
      hovered: Magenta
      inactive: Gray
      playbar_background: Black
      playbar_progress: Green
      playbar_text: White
      selected: LightCyan
      text: White
    behavior:
      seek_milliseconds: 5000
      volume_increment: 5
      tick_rate_milliseconds: 250
  '';

  # VLC configuration
  home.file.".config/vlc/vlcrc".text = ''
    # VLC media player preferences
    [core]
    # Interface settings
    intf=qt
    qt-minimal-view=0

    # Video settings
    video-title-show=1
    video-title-timeout=2000
    deinterlace=1
    deinterlace-mode=yadif

    # Audio settings
    audio-language=en
    volume-save=1

    # Subtitle settings
    sub-language=en
    sub-autodetect-file=1

    # Performance
    ffmpeg-hw=1
    avcodec-hw=any

    # Network
    http-reconnect=1
  '';

  # Discord configuration
  home.file.".config/discord/settings.json".text = builtins.toJSON {
    # Discord settings (basic, as most are stored server-side)
    SKIP_HOST_UPDATE = true;
    MINIMIZE_TO_TRAY = true;
    OPEN_ON_STARTUP = false;
  };

  # ===== Productivity Application Settings =====

  # Obsidian configuration
  # Note: Obsidian stores most settings in the vault, but we can set app preferences
  home.file.".config/obsidian/obsidian.json".text = builtins.toJSON {
    vaults = {
      # Add your vault paths here if you want them pre-configured
      # "vault-id" = {
      #   path = "/home/aural/Documents/ObsidianVault";
      #   open = true;
      # };
    };
    # App preferences
    theme = "obsidian";
    showRibbon = true;
    showLeftSidebar = true;
    showRightSidebar = true;
    alwaysUpdateLinks = true;
  };

  # LibreOffice configuration
  # Note: LibreOffice stores config in a complex structure, but we can set some defaults
  home.file.".config/libreoffice/4/user/registrymodifications.xcu".text = ''
    <?xml version="1.0" encoding="UTF-8"?>
    <oor:items xmlns:oor="http://openoffice.org/2001/registry" xmlns:xs="http://www.w3.org/2001/XMLSchema" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance">
      <!-- Save in ODF format by default -->
      <item oor:path="/org.openoffice.Office.Common/Filter/Microsoft/Export">
        <prop oor:name="SaveAlwaysAllowed" oor:op="fuse">
          <value>false</value>
        </prop>
      </item>
      <!-- Default font -->
      <item oor:path="/org.openoffice.Office.Writer/DefaultFont">
        <prop oor:name="Standard" oor:op="fuse">
          <value>Liberation Sans;12</value>
        </prop>
      </item>
      <!-- Auto-save every 10 minutes -->
      <item oor:path="/org.openoffice.Office.Common/Save/Document">
        <prop oor:name="AutoSave" oor:op="fuse">
          <value>true</value>
        </prop>
        <prop oor:name="AutoSaveTimeIntervall" oor:op="fuse">
          <value>10</value>
        </prop>
      </item>
    </oor:items>
  '';

  # Thunderbird configuration (if you decide to use it)
  # home.file.".thunderbird/profiles.ini".text = ''
  #   [Profile0]
  #   Name=default
  #   IsRelative=1
  #   Path=default.profile
  #   Default=1
  # '';

  # XDG configuration moved to xdg.nix
}

