# User settings
userSettings = {
  # Editor settings
  "editor.fontSize" = 14;
  "editor.fontFamily" = "'JetBrainsMono Nerd Font', 'JetBrains Mono', 'Cascadia Code', 'Fira Code', monospace";
  "editor.fontLigatures" = true;
  "editor.lineHeight" = 22;
  "editor.rulers" = [80 120];
  "editor.wordWrap" = "on";
  "editor.minimap.enabled" = false;
  "editor.renderWhitespace" = "selection";
  "editor.suggestSelection" = "first";
  "editor.acceptSuggestionOnCommitCharacter" = false;
  "editor.formatOnSave" = true;
  "editor.formatOnPaste" = true;
  "editor.inlineSuggest.enabled" = true;
  "editor.cursorStyle" = "line";
  "editor.smoothScrolling" = true;
  "editor.cursorBlinking" = "smooth";
  "editor.bracketPairColorization.enabled" = true;
  "editor.guides.bracketPairs" = "active";
  "editor.stickyScroll.enabled" = true;
  "editor.linkedEditing" = true;
  "editor.tabSize" = 2;

  # Workbench settings
  "workbench.colorTheme" = "Dracula";
  "workbench.iconTheme" = "material-icon-theme";
  "workbench.startupEditor" = "none";
  "workbench.sideBar.location" = "left";
  "workbench.activityBar.location" = "default";
  "workbench.statusBar.visible" = true;
  "workbench.tree.indent" = 15;
  "workbench.tree.renderIndentGuides" = "always";
  "workbench.editor.enablePreview" = false;
  "workbench.editor.revealIfOpen" = true;

  # Terminal settings
  "terminal.integrated.fontSize" = 14;
  "terminal.integrated.fontFamily" = "'JetBrainsMono Nerd Font', 'JetBrains Mono'";
  "terminal.integrated.defaultProfile.linux" = "bash";
  "terminal.integrated.cursorBlinking" = true;
  "terminal.integrated.cursorStyle" = "line";
  "terminal.integrated.smoothScrolling" = true;
  "terminal.integrated.copyOnSelection" = true;

  # File settings
  "files.autoSave" = "afterDelay";
  "files.autoSaveDelay" = 1000;
  "files.trimTrailingWhitespace" = true;
  "files.insertFinalNewline" = true;
  "files.trimFinalNewlines" = true;
  "files.eol" = "\n";
  "files.exclude" = {
    "**/.git" = false;
    "**/.DS_Store" = true;
    "**/node_modules" = true;
    "**/__pycache__" = true;
    "**/*.pyc" = true;
  };
  "files.watcherExclude" = {
    "**/.git/objects/**" = true;
    "**/.git/subtree-cache/**" = true;
    "**/node_modules/**" = true;
    "**/.venv/**" = true;
    "**/venv/**" = true;
  };

  # Explorer settings
  "explorer.confirmDelete" = false;
  "explorer.confirmDragAndDrop" = false;
  "explorer.compactFolders" = false;

  # Git settings
  "git.enableSmartCommit" = true;
  "git.confirmSync" = false;
  "git.autofetch" = true;
  "git.autoStash" = true;
  "git.openRepositoryInParentFolders" = "always";

  # Search settings
  "search.exclude" = {
    "**/node_modules" = true;
    "**/bower_components" = true;
    "**/*.code-search" = true;
    "**/.venv" = true;
    "**/venv" = true;
    "**/__pycache__" = true;
  };
  "search.useIgnoreFiles" = true;
  "search.followSymlinks" = false;

  # Extension settings
  "extensions.ignoreRecommendations" = false;
  "extensions.autoUpdate" = true;
  "extensions.autoCheckUpdates" = true;

  # Telemetry
  "telemetry.telemetryLevel" = "off";

  # Window settings
  "window.zoomLevel" = 0;
  "window.menuBarVisibility" = "toggle";

  # Update settings
  "update.mode" = "manual";
  "update.showReleaseNotes" = false;

  # Security
  "security.workspace.trust.untrustedFiles" = "open";

  # Language-specific formatters and settings
