# VS Code configuration
programs.vscode = {
  enable = true;
  package = pkgs.vscode;

  # Default profile with settings and extensions
  profiles = {
    default = {
      # User settings
      userSettings = {
    "editor.fontSize" = 13;
    "editor.fontFamily" = "'JetBrains Mono', 'Droid Sans Mono', monospace";
    "editor.fontLigatures" = true;
    "editor.lineHeight" = 22;
    "editor.rulers" = [80 120];
    "editor.wordWrap" = "on";
    "editor.minimap.enabled" = false;
    "editor.renderWhitespace" = "selection";
    "editor.suggestSelection" = "first";
    "editor.acceptSuggestionOnCommitCharacter" = false;

    "workbench.colorTheme" = "Dracula";
    "workbench.iconTheme" = "material-icon-theme";
    "workbench.startupEditor" = "none";

    "terminal.integrated.fontSize" = 13;
    "terminal.integrated.fontFamily" = "'JetBrains Mono'";
    "terminal.integrated.defaultProfile.linux" = "bash";

    "files.autoSave" = "afterDelay";
    "files.autoSaveDelay" = 1000;
    "files.trimTrailingWhitespace" = true;
    "files.insertFinalNewline" = true;

    "git.enableSmartCommit" = true;
    "git.confirmSync" = false;
    "git.autofetch" = true;

    "extensions.ignoreRecommendations" = false;
    "telemetry.telemetryLevel" = "off";

    # Language-specific settings
    "[python]" = {
      "editor.formatOnSave" = true;
      "editor.defaultFormatter" = "ms-python.black-formatter";
    };
    "[javascript]" = {
      "editor.formatOnSave" = true;
      "editor.defaultFormatter" = "esbenp.prettier-vscode";
    };
    "[typescript]" = {
      "editor.formatOnSave" = true;
      "editor.defaultFormatter" = "esbenp.prettier-vscode";
    };
    "[rust]" = {
      "editor.formatOnSave" = true;
    };
    "[nix]" = {
      "editor.formatOnSave" = true;
      "editor.defaultFormatter" = "jnoortheen.nix-ide";
    };
  };

      # Extensions to install
      extensions = with pkgs.vscode-extensions; [
    # Themes
    dracula-theme.theme-dracula
    pkief.material-icon-theme

    # General
    # editorconfig.editorconfig
    # streetsidesoftware.code-spell-checker
    # usernamehw.errorlens
    # gruntfuggly.todo-tree

    # Git
    eamodio.gitlens
    mhutchie.git-graph
    donjayamanne.githistory

    # Nix
    jnoortheen.nix-ide

    # Python
    ms-python.python
    ms-python.vscode-pylance
    ms-python.black-formatter
    ms-python.isort

    # JavaScript/TypeScript
    dbaeumer.vscode-eslint
    esbenp.prettier-vscode

    # Rust
    rust-lang.rust-analyzer
    tamasfe.even-better-toml
    # dependi (need to get actual ext name)

    # Go
    golang.go

    # Docker/Kubernetes
    ms-azuretools.vscode-docker
    ms-kubernetes-tools.vscode-kubernetes-tools

    # YAML/JSON
    redhat.vscode-yaml

    # Markdown
    yzhang.markdown-all-in-one
    bierner.markdown-mermaid

    # Remote Development
    # ms-vscode-remote.remote-ssh
    # ms-vscode-remote.remote-containers
      ];
    };
  };
};

# Browser configurations moved to browsers.nix

# ===== Media Application Settings =====

# Spotify configuration
# Note: Spotify doesn't have a Home Manager module, but we can set preferences
home.file.".config/spotify-tui/config.yml".text = ''
  # Spotify TUI configuration (if you use spotify-tui)
  theme:
    active: Cyan

