# modules/home-manager/plasma.nix - KDE Plasma configuration
{ config, pkgs, lib, ... }:

{
  # Install plasma-manager for declarative KDE configuration
  imports = [
    (builtins.fetchTarball {
      url = "https://github.com/nix-community/plasma-manager/archive/trunk.tar.gz";
      sha256 = "sha256:0yrsm1g93vvhbblrx2imhhx96zws7ak96qf3b20f4blrgnpqfzjr";
    })
  ];

  # Enable plasma configuration
  programs.plasma = {
    enable = true;

    # Configure notification settings
    configFile = {
      # Notification configuration
      plasmanotifyrc = {
        # Set notification popup position to bottom right
        Notifications = {
          # Position notifications in bottom right corner
          # Valid values: TopLeft, TopCenter, TopRight, CenterLeft, Center, CenterRight, BottomLeft, BottomCenter, BottomRight
          PopupPosition = "BottomRight";

          # Keep notifications on screen where mouse is located
          PopupScreen = "@Point";

          # Optional: Set spacing from screen edge (in pixels)
          PopupMargin = 10;

          # Optional: Configure popup timeout (milliseconds)
          PopupTimeout = 5000;

          # Show notifications in history
          ShowInHistory = true;

          # Keep critical notifications visible
          NormalAlwaysOnTop = false;
          CriticalAlwaysOnTop = true;
        };

        # Configure notification behavior
        DoNotDisturb = {
          # Don't show popups when fullscreen app is active
          WhenScreensMirrored = false;
          NotificationSounds = true;
        };

        # Application-specific notification settings can be added here
        # Example:
        # Applications = {
        #   # Configure specific app notifications
        # };
      };

      # Additional Plasma workspace settings
      kdeglobals = {
        # General KDE settings
        General = {
          # Optional: Configure widget style, colors, etc.
        };
      };

      # Panel and widget positioning (if needed)
      plasmashellrc = {
        # Configure panel and widget behavior
        # This can affect where notification widget appears
        PlasmaViews = {
          # Ensure notification widget is properly positioned
        };
      };
    };

    # Workspace configuration
    workspace = {
      # Click behavior
      clickItemTo = "select";

      # Theme settings (optional)
      # theme = "breeze-dark";
      # colorScheme = "BreezeDark";

      # Cursor theme (optional)
      # cursor = {
      #   theme = "breeze_cursors";
      #   size = 24;
      # };
    };

    # Shortcuts (optional, but useful for notification management)
    shortcuts = {
      # Configure global shortcuts for notifications
      "plasmashell" = {
        # Toggle do not disturb mode
        "toggle do not disturb" = "Meta+Shift+N";

        # Clear notifications
        "clear-notifications" = "Meta+Shift+C";

        # Show notification history
        "show-notification-history" = "Meta+N";
      };
    };

    # KWin window rules (can affect notification windows)
    window-rules = [
      {
        description = "Notification positioning";
        match = {
          window-class = {
            value = "plasmashell";
            type = "substring";
          };
          window-type = {
            value = "notification";
            type = "exact";
          };
        };
        apply = {
          # Ensure notifications float and don't get tiled
          above = {
            value = true;
            apply = "force";
          };
          skippager = {
            value = true;
            apply = "force";
          };
          skiptaskbar = {
            value = true;
            apply = "force";
          };
        };
      }
    ];
  };

  # Install additional notification-related packages
  home.packages = with pkgs; [
    # libnotify for sending test notifications
    libnotify

    # Optional: Alternative notification daemons (for testing)
    # dunst  # Lightweight notification daemon
  ];

  # Create a script to test notifications
  home.file.".local/bin/test-notification" = {
    executable = true;
    text = ''
      #!/usr/bin/env bash
      # Test notification script

      echo "Sending test notification..."
      notify-send "Test Notification" "This should appear in the bottom right corner" \
        --icon=dialog-information \
        --expire-time=5000

      echo "Sent! Check if it appeared in the bottom right corner."
      echo "You can also try:"
      echo "  - Critical: notify-send -u critical 'Critical' 'This is urgent'"
      echo "  - Low priority: notify-send -u low 'Low Priority' 'Not urgent'"
    '';
  };

  # Ensure notification service is running
  systemd.user.services.plasma-notifications = {
    Unit = {
      Description = "Ensure KDE notifications are properly configured";
      After = [ "graphical-session.target" ];
      PartOf = [ "graphical-session.target" ];
    };
    Service = {
      Type = "oneshot";
      RemainAfterExit = true;
      ExecStart = "/run/current-system/sw/bin/true";
    };
    Install = {
      WantedBy = [ "graphical-session.target" ];
    };
  };
}

