# Edit this configuration file to define what should be installed on
# your system.  Help is available in the configuration.nix(5) man page
# and in the NixOS manual (accessible by running ‘nixos-help’).

{ config, pkgs, lib, ... }:

{
  imports =
    [ # Include the results of the hardware scan.
      ./hardware-configuration.nix
      ./cachix.nix

      # Home Manager
      ./modules/home-manager

      # Development modules
      ./modules/development/tools.nix
      ./modules/development/languages.nix
      ./modules/development/direnv.nix
      ./modules/development/databases.nix
      # ./modules/development/neovim.nix  # Moved to Home Manager
      ./modules/development/productivity.nix

      # Desktop modules
      ./modules/desktop/desktop-selector.nix  # Desktop environment switcher
      ./modules/desktop/browsers.nix
      ./modules/desktop/applications.nix
      ./modules/desktop/fonts-themes.nix
      ./modules/desktop/gaming.nix

      # Hardware
      ./modules/hardware/bluetooth.nix

      # Services
      ./modules/services/docker.nix
      ./modules/services/ssh.nix
      ./modules/services/vpn.nix

      # System
      ./modules/system/auto-update-flakes.nix

      # Security
      ./modules/security/hardening.nix
    ];

  # Bootloader.
  boot.loader.systemd-boot.enable = true;
  boot.loader.efi.canTouchEfiVariables = true;

  networking.hostName = "sinistercat"; # Define your hostname.
  # networking.wireless.enable = true;  # Enables wireless support via wpa_supplicant.

  # Configure network proxy if necessary
  # networking.proxy.default = "http://user:password@proxy:port/";
  # networking.proxy.noProxy = "127.0.0.1,localhost,internal.domain";

  # Enable networking
  networking.networkmanager.enable = true;

  # Set your time zone.
  time.timeZone = "America/Chicago";

  # Select internationalisation properties.
  i18n.defaultLocale = "en_US.UTF-8";

  i18n.extraLocaleSettings = {
    LC_ADDRESS = "en_US.UTF-8";
    LC_IDENTIFICATION = "en_US.UTF-8";
    LC_MEASUREMENT = "en_US.UTF-8";
    LC_MONETARY = "en_US.UTF-8";
    LC_NAME = "en_US.UTF-8";
    LC_NUMERIC = "en_US.UTF-8";
    LC_PAPER = "en_US.UTF-8";
    LC_TELEPHONE = "en_US.UTF-8";
    LC_TIME = "en_US.UTF-8";
  };

  # Enable the X11 windowing system.
  # You can disable this if you're only using the Wayland session.
  services.xserver.enable = true;

  # Desktop environments are now managed in desktop-selector module
  # Edit modules/desktop/desktop-selector.nix to enable/disable desktop environments

  # Configure keymap in X11
  services.xserver.xkb = {
    layout = "us";
    variant = "colemak";
    options = "caps:backspace";
  };

  # Enable CUPS to print documents.
  services.printing.enable = true;

  # Enable sound with pipewire.
  services.pulseaudio.enable = false;
  security.rtkit.enable = true;
  services.pipewire = {
    enable = true;
    alsa.enable = true;
    alsa.support32Bit = true;
    pulse.enable = true;
  };

  # Enable touchpad support (enabled default in most desktopManager).
  # services.xserver.libinput.enable = true;

  # Define a user account. Don't forget to set a password with 'passwd'.
  users.users.aural = {
    isNormalUser = true;
    description = "Aural";
    extraGroups = [ "networkmanager" "wheel" ];
    packages = with pkgs; [
      kdePackages.kate
    ];
  };

  # Allow mutable users to set passwords after installation
  users.mutableUsers = lib.mkForce true;

  # Enable automatic login for the user.
  services.displayManager.autoLogin.enable = true;
  services.displayManager.autoLogin.user = "aural";

  # Install firefox.
  programs.firefox.enable = true;

  # Allow unfree packages
  nixpkgs.config.allowUnfree = true;

  # List packages installed in system profile. To search, run:
  # $ nix search wget
  environment.systemPackages = with pkgs; [
  #  wget  
];

  # Some programs need SUID wrappers, can be configured further or are
  # started in user sessions.
  # programs.mtr.enable = true;
  # programs.gnupg.agent = {
  #   enable = true;
  #   enableSSHSupport = true;
  # };

    # Enable flakes and new nix command
  nix = {
    settings = {
      experimental-features = [ "nix-command" "flakes" ];
      auto-optimise-store = true;
    };

    # Garbage collection
    gc = {
      automatic = true;
      dates = "weekly";
      options = "--delete-older-than 30d";
    };
  };

  # This value determines the NixOS release from which the default
  # settings for stateful data, like file locations and database versions
  # on your system were taken. It‘s perfectly fine and recommended to leave
  # this value at the release version of the first install of this system.
  # Before changing this value read the documentation for this option
  # (e.g. man configuration.nix or on https://nixos.org/nixos/options.html).
  system.stateVersion = "25.05"; # Did you read the comment?

}

# modules/desktop/desktop-selector.nix - Easy desktop environment switching
{ config, pkgs, lib, ... }:

let
  # Configure which desktop environments to enable
  # Change these to true to enable, false to disable
  desktopConfig = {
    # Full Desktop Environments
    plasma = true;      # KDE Plasma (currently enabled)
    gnome = false;      # GNOME
    xfce = false;       # XFCE - disabled to prevent notification conflicts with KDE
    cinnamon = false;   # Cinnamon
    mate = false;       # MATE
    budgie = false;     # Budgie
    pantheon = false;   # Pantheon

    # Window Managers
    i3 = false;         # i3 tiling WM
    awesome = false;    # Awesome WM
    hyprland = false;   # Hyprland (Wayland)
    sway = false;       # Sway (Wayland)

    # Display Manager
    # Only one should be true
    useSddm = true;     # KDE's display manager (works with all)
    useGdm = false;     # GNOME's display manager
    useLightdm = false; # Lightweight display manager
  };
in
{
  # Display Manager Configuration
  services.displayManager.sddm.enable = desktopConfig.useSddm;
  services.xserver.displayManager.gdm.enable = desktopConfig.useGdm;
  services.xserver.displayManager.lightdm.enable = desktopConfig.useLightdm;

  # Desktop Environments
  services.desktopManager.plasma6.enable = desktopConfig.plasma;  # NixOS 25.05 uses Plasma 6
  services.xserver.desktopManager.gnome.enable = desktopConfig.gnome;
  services.xserver.desktopManager.xfce.enable = desktopConfig.xfce;
  services.xserver.desktopManager.cinnamon.enable = desktopConfig.cinnamon;
  services.xserver.desktopManager.mate.enable = desktopConfig.mate;
  services.xserver.desktopManager.budgie.enable = desktopConfig.budgie;
  services.xserver.desktopManager.pantheon.enable = desktopConfig.pantheon;

  # Window Managers
  services.xserver.windowManager.i3 = lib.mkIf desktopConfig.i3 {
    enable = true;
    package = pkgs.i3-gaps;
    extraPackages = with pkgs; [
      i3status
      i3lock
      i3blocks
      rofi
      polybar
      dmenu
      dunst
      picom
      nitrogen
      xss-lock
    ];
  };

  services.xserver.windowManager.awesome = lib.mkIf desktopConfig.awesome {
    enable = true;
    luaModules = with pkgs.luaPackages; [
      vicious
    ];
  };

  programs.hyprland = lib.mkIf desktopConfig.hyprland {
    enable = true;
    xwayland.enable = true;
  };

  programs.sway = lib.mkIf desktopConfig.sway {
    enable = true;
    wrapperFeatures.gtk = true;
    extraPackages = with pkgs; [
      swaylock
      swayidle
      waybar
      wofi
      mako
      grim
      slurp
      wl-clipboard
    ];
  };

  # Additional packages based on what's enabled
  environment.systemPackages = with pkgs; lib.flatten [
    # GNOME extras
    (lib.optionals desktopConfig.gnome [
      gnome-tweaks
      gnome-themes-extra
      gnome-extensions-cli
    ])

    # XFCE extras
    (lib.optionals desktopConfig.xfce [
      xfce.xfce4-whiskermenu-plugin
      xfce.xfce4-pulseaudio-plugin
      xfce.xfce4-clipman-plugin
    ])

    # Hyprland extras
    (lib.optionals desktopConfig.hyprland [
      waybar
      wofi
      mako
      swaylock-effects
      swayidle
      grim
      slurp
      wl-clipboard
      swww  # Wallpaper daemon
      kitty # Terminal that works well with Wayland
    ])

    # Common tools for all environments
    (lib.optionals (desktopConfig.gnome || desktopConfig.xfce || desktopConfig.cinnamon || desktopConfig.mate) [
      networkmanagerapplet
    ])
  ];

  # Wayland support
  environment.sessionVariables = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    # Hint Electron apps to use Wayland
    NIXOS_OZONE_WL = "1";

    # Firefox Wayland support
    MOZ_ENABLE_WAYLAND = "1";

    # QT Wayland support
    QT_QPA_PLATFORM = "wayland";
    QT_WAYLAND_DISABLE_WINDOWDECORATION = "1";

    # SDL Wayland support
    SDL_VIDEODRIVER = "wayland";
  };

  # XDG portal configuration for Wayland compositors
  xdg.portal = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    enable = true;
    extraPortals = with pkgs; [
      xdg-desktop-portal-gtk
      xdg-desktop-portal-wlr
    ];
  };
}

# Instructions:
# 1. Edit the desktopConfig at the top of this file
# 2. Set desired desktop environments to true
# 3. Make sure only one display manager is true
# 4. Save and run: sudo nixos-rebuild switch
# 5. Restart and select your desktop at the login screen

# Note: You can have multiple desktop environments installed at once.
# The display manager will show a session selector to choose between them.

toolUseResult.originalFile:
# modules/desktop/desktop-selector.nix - Easy desktop environment switching
{ config, pkgs, lib, ... }:

let
  # Configure which desktop environments to enable
  # Change these to true to enable, false to disable
  desktopConfig = {
    # Full Desktop Environments
    plasma = true;      # KDE Plasma (currently enabled)
    gnome = false;      # GNOME
    xfce = false;       # XFCE - disabled to prevent notification conflicts with KDE
    cinnamon = false;   # Cinnamon
    mate = false;       # MATE
    budgie = false;     # Budgie
    pantheon = false;   # Pantheon

    # Window Managers
    i3 = false;         # i3 tiling WM
    awesome = false;    # Awesome WM
    hyprland = false;   # Hyprland (Wayland)
    sway = false;       # Sway (Wayland)

    # Display Manager
    # Only one should be true
    useSddm = true;     # KDE's display manager (works with all)
    useGdm = false;     # GNOME's display manager
    useLightdm = false; # Lightweight display manager
  };
in
{
  # Display Manager Configuration
  services.displayManager.sddm.enable = desktopConfig.useSddm;
  services.xserver.displayManager.gdm.enable = desktopConfig.useGdm;
  services.xserver.displayManager.lightdm.enable = desktopConfig.useLightdm;

  # Desktop Environments
  services.desktopManager.plasma6.enable = desktopConfig.plasma;  # NixOS 25.05 uses Plasma 6
  services.xserver.desktopManager.gnome.enable = desktopConfig.gnome;
  services.xserver.desktopManager.xfce.enable = desktopConfig.xfce;
  services.xserver.desktopManager.cinnamon.enable = desktopConfig.cinnamon;
  services.xserver.desktopManager.mate.enable = desktopConfig.mate;
  services.xserver.desktopManager.budgie.enable = desktopConfig.budgie;
  services.xserver.desktopManager.pantheon.enable = desktopConfig.pantheon;

  # Window Managers
  services.xserver.windowManager.i3 = lib.mkIf desktopConfig.i3 {
    enable = true;
    package = pkgs.i3-gaps;
    extraPackages = with pkgs; [
      i3status
      i3lock
      i3blocks
      rofi
      polybar
      dmenu
      dunst
      picom
      nitrogen
      xss-lock
    ];
  };

  services.xserver.windowManager.awesome = lib.mkIf desktopConfig.awesome {
    enable = true;
    luaModules = with pkgs.luaPackages; [
      vicious
    ];
  };

  programs.hyprland = lib.mkIf desktopConfig.hyprland {
    enable = true;
    xwayland.enable = true;
  };

  programs.sway = lib.mkIf desktopConfig.sway {
    enable = true;
    wrapperFeatures.gtk = true;
    extraPackages = with pkgs; [
      swaylock
      swayidle
      waybar
      wofi
      mako
      grim
      slurp
      wl-clipboard
    ];
  };

  # Additional packages based on what's enabled
  environment.systemPackages = with pkgs; lib.flatten [
    # GNOME extras
    (lib.optionals desktopConfig.gnome [
      gnome-tweaks
      gnome-themes-extra
      gnome-extensions-cli
    ])

    # XFCE extras
    (lib.optionals desktopConfig.xfce [
      xfce.xfce4-whiskermenu-plugin
      xfce.xfce4-pulseaudio-plugin
      xfce.xfce4-clipman-plugin
    ])

    # Hyprland extras
    (lib.optionals desktopConfig.hyprland [
      waybar
      wofi
      mako
      swaylock-effects
      swayidle
      grim
      slurp
      wl-clipboard
      swww  # Wallpaper daemon
      kitty # Terminal that works well with Wayland
    ])

    # Common tools for all environments
    (lib.optionals (desktopConfig.gnome || desktopConfig.xfce || desktopConfig.cinnamon || desktopConfig.mate) [
      networkmanagerapplet
    ])
  ];

  # Wayland support
  environment.sessionVariables = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    # Hint Electron apps to use Wayland
    NIXOS_OZONE_WL = "1";

    # Firefox Wayland support
    MOZ_ENABLE_WAYLAND = "1";

    # QT Wayland support
    QT_QPA_PLATFORM = "wayland";
    QT_WAYLAND_DISABLE_WINDOWDECORATION = "1";

    # SDL Wayland support
    SDL_VIDEODRIVER = "wayland";
  };

  # XDG portal configuration for Wayland compositors
  xdg.portal = lib.mkIf (desktopConfig.hyprland || desktopConfig.sway) {
    enable = true;
    extraPortals = with pkgs; [
      xdg-desktop-portal-gtk
      xdg-desktop-portal-wlr
    ];
  };
}

# Instructions:
# 1. Edit the desktopConfig at the top of this file
# 2. Set desired desktop environments to true
# 3. Make sure only one display manager is true
# 4. Save and run: sudo nixos-rebuild switch
# 5. Restart and select your desktop at the login screen

# Note: You can have multiple desktop environments installed at once.
