diff --git a/modules/development/languages.nix b/modules/development/languages.nix
index a2ef03d..cb76aaf 100644
--- a/modules/development/languages.nix
+++ b/modules/development/languages.nix
@@ -35,7 +35,8 @@

   # Environment variables for development
   environment.variables = {
-    GOPATH = "$HOME/go";
+    # GOPATH is not set - Go 1.16+ uses $HOME/go by default
+    # Setting GOPATH can cause issues with nix-shell environments
     PATH = "$PATH:$HOME/go/bin:$HOME/.cargo/bin";
   };

diff --git a/modules/hardware/bluetooth.nix b/modules/hardware/bluetooth.nix
index 957f975..274db7a 100644
--- a/modules/hardware/bluetooth.nix
+++ b/modules/hardware/bluetooth.nix
@@ -29,7 +29,7 @@

   # PulseAudio/PipeWire Bluetooth support (you're using PipeWire)
   # This is likely already enabled in your audio config, but let's ensure it
-  hardware.pulseaudio.enable = false;  # We use PipeWire
+  services.pulseaudio.enable = false;  # We use PipeWire

   # Packages for Bluetooth management
   environment.systemPackages = with pkgs; [
diff --git a/modules/home-manager/xdg.nix b/modules/home-manager/xdg.nix
index 20693cc..a97e5b6 100644
--- a/modules/home-manager/xdg.nix
+++ b/modules/home-manager/xdg.nix
@@ -265,7 +265,7 @@
     # Application-specific XDG overrides
     CARGO_HOME = "$XDG_DATA_HOME/cargo";
     RUSTUP_HOME = "$XDG_DATA_HOME/rustup";
-    GOPATH = "$XDG_DATA_HOME/go";
+    # GOPATH removed - Go 1.16+ uses $HOME/go by default, setting it causes issues
     NPM_CONFIG_USERCONFIG = "$XDG_CONFIG_HOME/npm/npmrc";
     NPM_CONFIG_CACHE = "$XDG_CACHE_HOME/npm";
     PYTHONSTARTUP = "$XDG_CONFIG_HOME/python/pythonrc";

